script:
return {
  n2t(v) {
    const [, symbol, value] = `${v}`.match(/^(\+|\-)?(.+)/);
    return (symbol === "-" ? "下跌" : "上涨") + value; 
  },
    n3t(v) {
    const [, symbol, value] = `${v}`.match(/^(\+|\-)?(.+)/);
    return [(symbol === "-" ? "净流出" : "净流入"),value]; 
  },
  chartSrc(v){
    return configSrc+v+'/ddcoption.json'
  },
  amendmentX(a){
    let i = 0;
    `${a}`.split('').forEach(value => {
        if(value.charCodeAt() < 126){ i=i+0.5;
        }
        else{
            i++;
        }
    })
    return i;
  }

}
